mapper.select:
select a.number, a.time, a.address, a.content, a.legalnum, a.price, a.score, a.agency, a.handlefee, a.illegalid, a.province, a.city, a.town, a.lat, a.lng, a.canhandle, a.create_time, a.update_time, a.lsprefix, a.lsnum, b.status, a.processfree, a.canProcess from tb_illegal a left join tb_illegal_process b on a.number=b.number
AND a.lsprefix =#{lsprefix}
AND a.lsnum =#{lsnum}
order by a.time desc

select *,last_query_time as 'lastQueryTime' from tb_car


        AND lsprefix =#{lsprefix}


        AND lsnum =#{lsnum}

mapper.insert:
insert into tb_illegal_process select * from tb_illegal
where number in

    #{id}

update  tb_illegal_process status set `status` = 1
where number in

    #{id}

mapper.select:
select *
from tb_illegal_process


        AND lsprefix =#{lsprefix}


        AND lsnum =#{lsnum}


order by time desc
